import { Modal, Button } from "react-bootstrap";
import React from "react";
import ReactDOM from 'react-dom';
import "../static/popup.css";

class Popup extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            show: true,
            useremail: ""
        };

        this.handleClose = this.handleClose.bind(this);
        this.handleShow = this.handleShow.bind(this);
    }

    handleClose (){
        this.setState({ show: false });
    };

    handleShow (){
        this.setState({ show: true });
    };

    submitForm (event){
        alert("hiiii");
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
        alert(this.state.useremail);
    }

    setUserEmail = (event) => {
        alert("hiiii");
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
        alert(this.state.useremail);
    }

    render() {
        return(
            <div>
                {/* <Button variant="primary" onClick={this.handleShow}>Launch modal</Button> */}
                <Modal show={this.state.show} onHide={this.handleClose}>
                    <div id="popup">
                    <Modal.Header closeButton>
                        <Modal.Title>Enter your email to get 10% off!</Modal.Title>
                    </Modal.Header>
                    <form>
                    <Modal.Body>
                        <label>
                            Your Email: 
                            <input type="text" name="useremail" onChange={this.setUserEmail}/>
                        </label>
                        <input type="submit" value="Submit" onClick={this.submitForm}/>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>Close</Button>
                        <Button variant="primary">Submit</Button>
                    </Modal.Footer>
                    </form>
                    </div>
                </Modal>
            </div>
        );
    }
}

ReactDOM.render(<Popup />, document.getElementById('root'));
export default Popup;
